// Advanced secure link generation with JWT and analytics tracking
const jwt = require('jsonwebtoken');
const crypto = require('crypto');

const properties = $input.all();
const results = [];
const userPhone = $('Parse WhatsApp Data').item.json.phone;

for (const property of properties) {
  // Generate secure JWT token with expiration
  const token = jwt.sign(
    {
      propertyId: property.json.id,
      recipientPhone: userPhone,
      accessLevel: 'view',
      exp: Math.floor(Date.now() / 1000) + (10 * 60), // 10 minutes
      iat: Math.floor(Date.now() / 1000)
    },
    process.env.JWT_SECRET || 'your-ultra-secure-secret-key-change-this'
  );

  // Generate unique tracking ID
  const trackingId = crypto.randomUUID();

  const secureLink = `https://your-domain.com/secure-property/${token}?track=${trackingId}`;

  results.push({
    ...property.json,
    secureLink,
    trackingId,
    expiresAt: new Date(Date.now() + 10 * 60 * 1000).toISOString(),
    accessCount: 0,
    generatedFor: userPhone
  });
}

return results;

