const message = $input.first().json.messages[0] ;

let content = "";
let media_type = message?.type;

switch (media_type) {
  case "text":
    content = message?.text?.body;
    break;
  case "button":
    content = message?.button?.text;
    break;
  case "interactive":
    content = message?.interactive?.button_reply?.title || message?.interactive?.list_reply?.title;
    break;
  case "location":
    content = `${message?.location?.latitude}, ${message?.location?.longitude}`;
    requires_processing = true;
    break;
  case "image":
  case "video":
  case "audio":
  case "document":
    content = message[media_type]?.caption || `Received a ${media_type}`;
    break;
  default:
    content = "Unsupported message type.";
}

return [
  {
    json: {
      normalized_text: content,
      media_type,
    }
  }
];

