const inputItems = $input.all();

const nearbyData = {
  hospitals: [],
  schools: [],
  gasStations: [],
  markets: [],
  otherLandmarks: [],
};

for (const item of inputItems) {
  const results = item.json.results || [];

  for (const place of results.slice(0, 5)) {
    const placeInfo = {
      name: place.name,
      address: place.vicinity || place.formatted_address || null,
      rating: place.rating || null,
      location: place.geometry?.location || {},
      types: place.types || [],
    };

    const types = place.types || [];

    if (types.includes('hospital')) {
      nearbyData.hospitals.push(placeInfo);
    } else if (types.includes('school')) {
      nearbyData.schools.push(placeInfo);
    } else if (types.includes('gas_station')) {
      nearbyData.gasStations.push(placeInfo);
    } else if (types.includes('supermarket') || types.includes('store')) {
      nearbyData.markets.push(placeInfo);
    } else {
      nearbyData.otherLandmarks.push(placeInfo);
    }
  }
}

return [{ json: { aiPromptData: nearbyData } }];

